import React from 'react';
import { StyleSheet, View, Button, Text ,Dimensions, Image} from 'react-native';
import { AntDesign } from '@expo/vector-icons';

const InventoryCard = props =>{
    return(
        <View style={styles.cardContainer}>
            <Image style={styles.imageStyle} source={require('../images/fish.jpg')}/>

                <Text>{props.card.title1}</Text>
                <Text>{props.card.title2}</Text>
                <Text>{props.card.title3}</Text>
            <AntDesign name="arrowright" size={24} color="black" />
        </View>
        
    )
}

const deviceWidth = Math.round(Dimensions.get('window').width);
const styles = StyleSheet.create({
    cardContainer: {
        width: deviceWidth - 15,
        height: 90,
        borderRadius: 30,
        shadowColor: '#00000029',
        shadowRadius: 30,
    },
    imageStyle:{
        height: 50,
        width: 38,
    },
});



export default InventoryCard;
