import React, { useState } from "react";
import { View, Text, TextInput, Picker, TouchableOpacity } from "react-native";

const AddStorageUnit = () => {
  const [unitName, setUnitName] = useState("");
  const [temperature, setTemperature] = useState(null);
  const [currentEmoji, setCurrentEmoji] = useState(null);
  const [unitNameError, setUnitNameError] = useState(null);
  const [temperatureError, setTemperatureError] = useState(null);
  const [emojiError, setEmojiError] = useState(null);

  const validateForm = () => {
    let valid = true;
    setUnitNameError(null);
    setTemperatureError(null);
    setEmojiError(null);

    if (unitName.trim().length === 0) {
      setUnitNameError("Unit name is required");
      valid = false;
    }

    if (temperature === null) {
      setTemperatureError("Temperature is required");
      valid = false;
    }

    if (currentEmoji === null) {
      setEmojiError("Please select an emoji");
      valid = false;
    }

    return valid;
  };

  const handleSave = () => {
    if (validateForm()) {
      // אם הטופס תקין, לבצע את הפעולות הנדרשות לשמירת היחידת האחסון
    }
  };

  const handleEmojiSelect = (emoji) => {
    setCurrentEmoji(emoji);
    setEmojiError(null);
  };

  return (
    <View>
      <TextInput
        placeholder="Unit Name"
        value={unitName}
        onChangeText={setUnitName}
      />
      {unitNameError && <Text>{unitNameError}</Text>}

      {/* כאן מתבצע רנדור של הקומפוננטה EmojiComp תמיד */}
      <EmojiComp handleEmojiSelect={handleEmojiSelect} />
      {emojiError && <Text>{emojiError}</Text>}

      <Picker
        selectedValue={temperature}
        onValueChange={(itemValue) => setTemperature(itemValue)}
      >
        <Picker.Item label="Choose Type" value={null} />
        <Picker.Item label="Cold" value="Cold" />
        <Picker.Item label="Cool" value="Cool" />
        <Picker.Item label="Warm" value="Warm" />
      </Picker>
      {temperatureError && <Text>{temperatureError}</Text>}

      <TouchableOpacity onPress={handleSave}>
        <Text>Save</Text>
      </TouchableOpacity>
    </View>
  );
};

export default AddStorageUnit;
