import React from 'react';
import { StyleSheet, View, Button, Text, Image, Dimensions, ScrollView} from 'react-native';
import { useState, useEffect } from 'react';
import emoji from '../JSON/emojiJson.json'
//====================================================================
import {getEmojis} from '../axios/accessServer';
//====================================================================
const EmojiComp = ({ CallbackFunction, lastEmoji }) => {
    const [currentEmoji, setCurrentEmoji] = useState(lastEmoji);
    const [emojiList, setEmojiList] = useState([]);



//-----------------------------------------
useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await getEmojis();
        let x = response.data;
        setEmojiList(x);
      } catch (error) {
        console.error(error);
      }
    };
    fetchData();
  }, []);
   {console.log(emoji);}
    return(
        <View style={styles.container}>

            {CallbackFunction(currentEmoji)}
            
            <Text style={styles.title}>Choose a new emoji</Text>




                {emojiList.map((emoji, key) => {
                return (
                    <View key={emoji.emojiId}>
                        <Text style={styles.emoji} onPress={()=>{
                            setCurrentEmoji(emoji.emoji);
                            }}>{emoji.emoji}</Text>
                    </View>
                    );})}


            
        </View>


    );
}
const deviceWidth = Math.round(Dimensions.get('window').width);
const deviceHeight = Math.round(Dimensions.get('window').height);

const styles = StyleSheet.create({
    container: {
        backgroundColor: '#FFFFFF',
        width: deviceWidth,
        borderRadius: 15,
        shadowColor: '#0000009D',
        shadowRadius: 30,
        flexDirection: 'row',
        flex: 1,
        flexWrap: 'wrap',
        maxWidth: 600,

    },
    title:{
        fontFamily: 'GeneralSans-Semibold',
        fontWeight: 'bold',
        position: 'relative',
        width: deviceWidth,
        left: deviceWidth/4,

    },
    emoji:{
        fontSize: 20,
        marginHorizontal: 10,
        
    },
    allEmojis:{
        
    }
});

export default EmojiComp;
