import React from 'react';
import { StyleSheet, View, Button, Text, Image, Dimensions } from 'react-native';
import { useState, useEffect } from 'react';
import Emoji from 'react-native-emoji';

//====================================================================
//====================================================================
const EmojiComp = ({navigation}) => {



    return(
        <View style={styles.container}>
            <Emoji name="coffee" style={{fontSize: 20}} />
            <Emoji name="tea" style={{fontSize: 20}} />
            <Emoji name="banana" style={{fontSize: 20}} />
            <Emoji name="meat" style={{fontSize: 20}} />




            
        </View>
    );
}
const deviceWidth = Math.round(Dimensions.get('window').width);
const deviceHeight = Math.round(Dimensions.get('window').height);

const styles = StyleSheet.create({
    container: {
        backgroundColor: '#FFFFFF',
        width: deviceWidth - 30,
        borderRadius: 30,
        shadowColor: '#00000029',
        shadowRadius: 30,
        flexDirection: 'row',
    },
});

export default EmojiComp;
