import React from 'react';
import { StyleSheet, View, Button, Text ,Dimensions, Image} from 'react-native';
import { AntDesign } from '@expo/vector-icons';
//================================================
var backgroundColorCard = function (percentColor) {
    let color;
    if (percentColor <= 30) {
        color = '#C41C21';
    }
    if (percentColor > 30 && percentColor <= 70) {
        color = '#EF9300';
    }
    if (percentColor > 70) {
        color = '#28AA29';
    }

    return color;
}
//================================================
const InventoryCard = props =>{
    return(
        <View style={styles.cardContainer} style={[styles.color, { backgroundColor: backgroundColorCard(props.card.inventoryLevelPercent) }, { width: props.card.inventoryLevelPercent + '%' }]}>

            <Text style={styles.imageStyle}>{props.card.emoji}</Text>

            <View style={styles.textStyle}>
                <Text style={styles.textStyle1}>{props.card.title1}</Text>
                <Text style={styles.textStyle2}>{props.card.title2}</Text>
                <Text style={styles.textStyle3}>{props.card.title3}</Text>
            </View>

            <AntDesign style={styles.iconStyle} name="arrowright" size={24} color="black" />
        </View>
        
    )
}

const deviceWidth = Math.round(Dimensions.get('window').width);
const styles = StyleSheet.create({
    cardContainer: {
        width: deviceWidth - 30,
        height: 90,
        borderRadius: 30,
        shadowColor: '#00000029',
        shadowRadius: 30,
        flexDirection: 'row',
    },
    color:{
        opacity: 0.26,
        borderTopLeftRadius:30,
        borderBottomLeftRadius: 30,


    },
    imageStyle:{
        position: 'absolute',
        left: '8%',
        top: '25%',
        flex: 1,
        fontSize:38
    },
    textStyle:{
        flex: 2,
        position: 'absolute',
        left: '30%',
        top: '20%'
    },
    textStyle1:{
        fontFamily: 'GeneralSans',
        fontSize: 16,
        fontWeight: 'bold',
        color: '#000000',
    },
    textStyle2: {
        fontFamily: 'GeneralSans-Semibold',
        fontSize: 14,
        color: '#A2142C',
        fontWeight: 'bold',

    },
    textStyle3: {
        fontFamily: 'GeneralSans-Medium',
        fontSize: 11,
        fontWeight: 'bold',
        color: '#000000',

    },
    iconStyle:{
        flex: 3,
        position: 'absolute',
        left: '90%',
        top: '30%'
    }
});



export default InventoryCard;
