import React from 'react';
import { StyleSheet, View, Button, Text, Image, Dimensions } from 'react-native';
import Card from './InventoryCard';
import { Picker } from '@react-native-picker/picker';
import { useState, useEffect } from 'react';
import {getInventory} from '../axios/accessServer';//Calling a function from another file
//====================================================================

//====================================================================
const Inventory = ({navigation}) => {
    const [selectedValue, setSelectedValue] = useState(1);
    const [cards, setCards] = useState([]);
    const [idUser, setIdUser] = useState(0);


    /*useEffect(() => {
        //console.log('calanit');
        //console.log(selectedValue);
        if (selectedValue === 1)
        {
            
        }
        if (selectedValue === 2) {

        }
        if (selectedValue === 3) {

        }
    },[selectedValue]);*/
    //====================


    useEffect(() => 
    {
        const fetchData = async () => {
          try {
            const response = await getInventory();
            let x = response.data;
            //console.log(x.length);
            setCards(x);
            setIdUser(x[0].IDUser);
                        
          } catch (error) {
            console.error(error);
        }
    };
    fetchData();
}, [cards]);
   
     
    return(

        <View style={styles.container}>

            <View style={styles.titleAndPicker}>
                <Text style={styles.titleStile}>Manage Inventory </Text>

                <Picker 
                    selectedValue={selectedValue} style={styles.pickerStyle}
                    onValueChange={(itemValue, itemIndex) =>
                        setSelectedValue(itemValue)
                        }>
                    
                    <Picker.Item label="Sort by Name" value="1" />
                    <Picker.Item label="Stock Level" value="2" />
                    <Picker.Item label="Type" value="3" />
                </Picker>
                
            </View>
            
            {cards.map((card, key) => {
                return (
                    <View  key={card.Inventoryid} >
                        <Card  card={card}/>

                        {key === cards.length - 1 && (
                <View style={styles.buttonStyle}>
                    <Button
                    color="#C3001F"
                    onPress={() =>
                    navigation.navigate('AddStorageUnit', { idUser: idUser })
                }
                title="+ Add Storage Unit"
                />
                </View>
                )}

                    </View>
                );
            })}


            
           


           


            
        </View>
    );
}
const deviceWidth = Math.round(Dimensions.get('window').width);
const deviceHeight = Math.round(Dimensions.get('window').height);

const styles = StyleSheet.create({
        container: {
          height: deviceHeight,
          alignItems: 'center',
          backgroundColor: '#FFFFFF',
          flex: 1,
        },
        titleAndPicker:{
          flexDirection: 'row',
          justifyContent: 'space-between',
          width:'90%',
          top: '3%',
          marginBottom: 8, // מרווח בין התיבת כותרת והפיקר
        },
        titleStile:{
          fontFamily: 'GeneralSans-Semibold',
          fontSize:17,
          fontWeight: 'bold',
        },
        pickerStyle:{
          borderRadius: 10,
          shadowColor: '#00000022',
          borderWidth: 0, 
          overflow: "hidden", 
          shadowRadius: 11,
          fontWeight: 'bold',
          fontFamily: 'GeneralSans-Bold',
          marginBottom: 8, // מרווח בין הפיקר לכרטיסיות
        },
        card:{
          width: '92%',
          maxWidth: '92%',
          alignItems: 'center',
          marginBottom: 8, // מרווח בין הכרטיסיות
        },
        buttonStyle: {
          fontFamily: 'GeneralSans',
          fontWeight: 'bold',
          position: 'absolute',
          top: deviceHeight,
          width: "80%",
          borderRadius: 15,
          backgroundColor: '#C3001F',
          borderRadius: 15,
          alignItems: 'center',
          justifyContent: 'center',
          marginVertical: 10, 
          marginHorizontal: 20
        }
      });
      

export default Inventory;
