import React from 'react';
import { StyleSheet, View, Button, Text, Image, Dimensions } from 'react-native';
import Card from './InventoryCard';
import { Picker } from '@react-native-picker/picker';
import { useState } from 'react';
//====================================================================



//====================================================================
const cards = [
    {
        id: '1',
        picture: '',
        title1: "Seafood Freezer",
        title2: "Low Stock",
        title3: "Low on 2/3 product types",
        icon: '',
        inventoryLevelPercent: 30,
        emoji:'ðŸ¦',
    },
    {
        id: '2',
        picture: '' ,
        title1: "Meat Freezer",
        title2: "Limited stock",
        title3: "Low on 1/3 product types",
        icon: '',
        inventoryLevelPercent: 50,
        emoji: 'ðŸ¥©',
    },
    {
        id: '3',
        picture: '',
        title1: "Vegetable Fridge",
        title2: "Sufficient Stock",
        title3: "All product types stocked",
        icon: '',
        inventoryLevelPercent: 75,
        emoji: 'ðŸ¥¬',

    },
    {
        id: '4',
        picture: '',
        title1: "Chicken Fridge",
        title2: "Limited stock",
        title3: "Low on 1/3 product types",
        icon: '',
        inventoryLevelPercent: 55,
        emoji: 'ðŸ—',

    },
    {
        id: '5',
        picture: '',
        title1: "Pasta Cabinet",
        title2: "Sufficient Stock",
        title3: "All product types stocked",
        icon: '' ,
        inventoryLevelPercent: 100,
        emoji: 'ðŸ',

    }
];
//====================================================================
const Inventory = ({navigation}) => {
    const [selectedLanguage, setSelectedLanguage] = useState();

    return(
        <View style={styles.container}>
            <Text style={styles.titleStile}>Manage Inventory </Text>

            <Picker
                selectedValue={selectedLanguage}
                onValueChange={(itemValue, itemIndex) =>
                    setSelectedLanguage(itemValue)
                }>
                <Picker.Item label="Java" value="java" />
                <Picker.Item label="JavaScript" value="js" />
            </Picker>

            {cards.map((card, key) => {
                return (
                    <View key={card.id} style={styles.card}>
                        <Card style={styles.cardStyle} card={card}/>

                    </View>
                );
            })}
            
            <View style={styles.buttonStyle}>
                <Button color="#C3001F" onPress={() => 
                        navigation.navigate('StorageUnit')} title='+ Add Storage Unit'></Button>
            </View>
        </View>
    )
}
const deviceWidth = Math.round(Dimensions.get('window').width);
const styles = StyleSheet.create({
    container: {
        backgroundColor: '#fff',
        alignItems: 'center',
        gap: 8,
        //shadowColor: '#00000029',

    },
    titleStile:{
        fontFamily: 'General Sans, Semibold',
        fontSize:17,
        fontWeight: 'bold',
       
    },
    cardStyle:{
    },
    buttonStyle: {
        fontFamily: 'General Sans',
        fontWeight: 'bold',
        position: 'absolute',
        top: '110%',
        width: deviceWidth - 150,
        borderRadius: 15,
        
        backgroundColor: '#C3001F',
        borderRadius: 15,
        alignItems: 'center',
        justifyContent: 'center',
    }
});

export default Inventory;
