import React from 'react';
import { StyleSheet, View, Button, Text, Image, Dimensions } from 'react-native';
import Card from './InventoryCard';
import { Picker } from '@react-native-picker/picker';
import { useState, useEffect } from 'react';
//import cards from '../JSON/inventory.json';
import {getInventory} from '../axios/accessServer';//Calling a function from another file
//let cards;
let array = [];
let idUser;
//====================================================================

//====================================================================
const Inventory = ({navigation}) => {
    const [selectedValue, setSelectedValue] = useState(1);
    const [cards, setCards] = useState([]);
    //const [idUser, setIdUser] = useState();


    /*useEffect(() => {
        //console.log('calanit');
        //console.log(selectedValue);
        if (selectedValue === 1)
        {
            
        }
        if (selectedValue === 2) {

        }
        if (selectedValue === 3) {

        }
    },[selectedValue]);*/
    //====================

    
    useEffect(async()=>{
        //async function fetchData()
        //{
            const response = (await getInventory());
            let x = response.data;
            setCards(...cards, x);
            array = x;
            console.log(array); 
            console.log(cards); 
            idUser = array[0].IDUser;
        //}
        //fetchData()
    })
   
    
    

    return(
        <View style={styles.container}>

            <View style={styles.titleAndPicker}>
                <Text style={styles.titleStile}>Manage Inventory </Text>

                <Picker 
                    selectedValue={selectedValue} style={styles.pickerStyle}
                    onValueChange={(itemValue, itemIndex) =>
                        setSelectedValue(itemValue)
                        }>
                    
                    <Picker.Item label="Sort by Name" value="1" />
                    <Picker.Item label="Stock Level" value="2" />
                    <Picker.Item label="Type" value="3" />
                </Picker>
                
            </View>
            
            {array.map((card, key) => {
                return (
                    <View  key={card.Inventoryid} >
                        <Card  card={card}/>
                    </View>
                );
            })}


            
            
            <View style={styles.buttonStyle}>
                <Button color="#C3001F" onPress={() => 
                        navigation.navigate('AddStorageUnit', {idUser: idUser})} title='+ Add Storage Unit'></Button>
            </View>
            
        </View>
    );
}
const deviceWidth = Math.round(Dimensions.get('window').width);
const deviceHeight = Math.round(Dimensions.get('window').height);

const styles = StyleSheet.create({
    container: {
        height: deviceHeight,
        alignItems: 'center',
        gap: 8,
        backgroundColor: '#FFFFFF',
    },
    titleAndPicker:{
        flexDirection: 'row',
        justifyContent: 'space-between',
        width:'90%',
        top: '3%',
    },
    titleStile:{
        fontFamily: 'GeneralSans-Semibold',
        fontSize:17,
        fontWeight: 'bold',

    },
    pickerStyle:{
        borderRadius: 10,
        shadowColor: '#00000022',
        borderWidth: 0, 
        overflow: "hidden", 
        shadowRadius: 11,
        fontWeight: 'bold',
        fontFamily: 'GeneralSans-Bold',

    },
   /* card:{
        width: '92%',
        maxWidth: '92%',
        alignItems: 'center'
    },*/
    buttonStyle: {
        fontFamily: 'GeneralSans',
        fontWeight: 'bold',
        position: 'absolute',
        top: '85%',
        width: "90%",
        borderRadius: 15,
        
        backgroundColor: '#C3001F',
        borderRadius: 15,
        alignItems: 'center',
        justifyContent: 'center',
    }
});

export default Inventory;
