import React from 'react';
import { StyleSheet, View, Button, Text, Image, Dimensions, TouchableOpacity } from 'react-native';
import { MaterialIcons } from '@expo/vector-icons';
import { MaterialCommunityIcons } from '@expo/vector-icons';
//====================================================================




//====================================================================
const StorageUnitLowStock = () => {

    return (
        <View style={styles.container}>
            <View style={styles.line1Style}>
                <Text style={styles.text1Styles}>Seafood Freezer</Text>
                
                <View style={styles.rectangle1Style}>
                    <View style={{ flex: 1, justifyContent: "center", alignItems: "flex-end"}}>
                        <MaterialIcons name="settings" size={12} color="white" />
                    </View>

                    <View style={{ flex: 2, justifyContent: "center", alignItems: "center" }}>

                        <Text>
                            <TouchableOpacity
                                onPress={() => { }}
                            >
                                <Text style={styles.rectangleTextStyle1}>Edit </Text>
                            </TouchableOpacity>

                        </Text>
                    </View>
                </View>
            </View>


            <View style={styles.line2Style}>

                <View style={styles.rectangle2Style}>
                    <View style={{ flex: 1, justifyContent: "center", alignItems: "flex-end" }}>
                        <MaterialCommunityIcons name="alert-decagram" size={12} color="white" />
                    </View>

                    <View style={{ flex: 2, justifyContent: "center", alignItems: "center" }}>

                        <Text>
                            
                            <Text style={styles.rectangleTextStyle2}>LOW STOCK </Text>

                        </Text>
                    </View>
                </View>

                <View style={styles.rectangle3Style}>
                    <View style={{ flex: 1, justifyContent: "center", alignItems: "flex-end" }}>
                        <MaterialCommunityIcons name="snowflake" size={12} color="white" />
                    </View>

                    <View style={{ flex: 2, justifyContent: "center", alignItems: "center" }}>

                        <Text>

                            <Text style={styles.rectangleTextStyle3}>COLD UNIT </Text>

                        </Text>
                    </View>
                </View>


                <View style={styles.rectangle4Style}>
                    <View style={{ flex: 2, justifyContent: "center", alignItems: "center" }}>

                        <Text>

                            <Text style={styles.rectangleTextStyle4}>-18Â° C</Text>

                        </Text>
                    </View>
                </View>

            </View>

            <View style={styles.box}></View>

           
        </View>
    )
}
const deviceWidth = Math.round(Dimensions.get('window').width);
const styles = StyleSheet.create({
    container: {
        backgroundColor: '#FFFFFF',
        alignItems: 'center',
        gap: 8,
    },
    line1Style:{
        flexDirection: 'row',
        justifyContent: 'space-between',
        width: '90%',
    },
    text1Styles:{
        fontFamily: 'GeneralSans-Bold',
        fontSize: 25,
        color: '#000000',
        fontWeight: 'bold',
    },
    rectangle1Style:{
        flexDirection: "row",
        justifyContent: 'space-between',
        backgroundColor: '#000000',
        width: '20%',
        borderRadius:10,
        shadowColor: '#00000029',
        shadowRadius: 30,
    },
    rectangleTextStyle1:{
        color: "white", 
        fontFamily: 'GeneralSans-Semibold', 
        fontSize: 11,

    },
    line2Style:{
        flexDirection: 'row',
        justifyContent: 'space-between',
        width: '90%',
    },
    rectangle2Style:{
        flexDirection: "row",
        justifyContent: 'space-between',
        backgroundColor: '#C31B20',
        borderRadius: 10,
        width: '30%',
    },
    rectangleTextStyle2: {
        color: "white", 
        fontFamily: 'GeneralSans-Semibold', 
        fontSize: 10
        
    },
    rectangle3Style: {
        flexDirection: "row",
        justifyContent: 'space-between',
        backgroundColor: '#0077B7',
        borderRadius: 10,
        width: '30%',
    },
    rectangleTextStyle3: {
        color: "white",
        fontFamily: 'GeneralSans-Semibold',
        fontSize: 10

    }, 
    rectangle4Style: {

        backgroundColor: '#EDEDED',
        borderRadius: 10,
        width: '20%',
    },
    rectangleTextStyle4: {
        fontFamily: 'GeneralSans-Medium',
        fontSize: 11

    },
    box:{
        gap: 20,
        
        width: deviceWidth -30,
        height: 313.94,
        backgroundColor: '#FFFFFF',
        //borderWidth: 1,
        opacity: '100%',
        borderRadius: 15,
        shadowColor: '#00000029',
        shadowRadius: 26,
    }
});

export default StorageUnitLowStock;
