import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, View, Text, TextInput, Button, Dimensions} from 'react-native';
import { Formik } from 'formik';
import * as yup from 'yup';
import { useState, useEffect } from "react";
import { Picker } from '@react-native-picker/picker';
import { AntDesign } from '@expo/vector-icons';
import EmojiComp from './EmojiComp';
import { useNavigation } from '@react-navigation/native';

import {createStorageUnit} from '../axios/accessServer';


//=====================================================
export default function AddStorageUnit({ route, ...props }) {
    const navigation = useNavigation();
    


    const [title, setTitle] = useState("New Storage Unit");
    const [unitName, setUnitName] = useState("");
    const [temperature, setTemperature] = useState();
    const [selectedValue1, setSelectedValue1] = useState("Choose Type");
    const [selectedValue2, setSelectedValue2] = useState("Choose Type");
    const [selectedValue3, setSelectedValue3] = useState("Choose Type");
    const [selectEmoji, setSelectEmoji] = useState(false);
    const [currentEmoji, setCurrentEmoji] = useState(null);
//============================================================
    const validationSchema = yup.object().shape({
        name: yup.string().required('שדה חובה'),
        email: yup.string().email('כתובת דוא"ל לא תקינה').required('שדה חובה'),
      });

//============================================================
const CallbackFunction = (emoji) => {
    useEffect(()=>{
        setCurrentEmoji(emoji);
    })
  };
  
  useEffect(() => {
    setSelectEmoji(false);
  },[currentEmoji]);
  //===========================================================
    const sendData = async ()=>{
        //console.log(route.params.idUser);
        
        let json = {
            idUser: route.params.idUser,
            emoji: currentEmoji,
            typeUnit: selectedValue1,
            temperature: temperature,
            allergyInformation: selectedValue2,
            alertWhenStockIs: selectedValue3,
            unitName: unitName,
        };
        let response = await createStorageUnit(json);
        console.log(response);
        return "Inventory";
    }
//===========================================================
    return (
    <Formik
      initialValues={{ name: '', email: '' }}
      validationSchema={validationSchema}
      onSubmit={(values) => {
        // הכנס את קוד הטיפול בשליחת הטופס כאן
        console.log(values);
      }}
    >

        <View style={styles.container}>
            

            
            <Text style={styles.title} >{title}</Text>

            <Text style={styles.emoji}>{currentEmoji}</Text>
            <Text style={styles.txt} onPress={() => setSelectEmoji(!selectEmoji)}>Choose Emoji</Text>

            <View style={styles.emojis}>{selectEmoji? <EmojiComp CallbackFunction={CallbackFunction} lastEmoji={currentEmoji}/> : null}</View>


            <Text style={styles.txt1}>Unit Name</Text>
            <TextInput style={styles.input1} onChangeText={text => setUnitName(text)}></TextInput>

            <Text style={styles.txt2}>Unit Type</Text>
            <Picker selectedValue={selectedValue1} style={styles.input2}
                onValueChange={(itemValue, itemIndex) => setSelectedValue1(itemValue)}
            >
                <Picker.Item label={selectedValue1} value="0" requierd />
                <Picker.Item label="Cold Unit" value='cold' />
                <Picker.Item label="Pantry Unit" value='pantry' />
            </Picker>

            <Text style={styles.txt3}>Temperature</Text>
            <TextInput style={styles.input3}  onChangeText={text => setTemperature(text)} />

            <Text style={styles.txt4}>Allergy Information</Text>
            <Picker selectedValue={selectedValue2} style={styles.input4}
                onValueChange={(itemValue, itemIndex) => setSelectedValue2(itemValue)}
            >
                <Picker.Item label={selectedValue2} value="0" requierd/>
                <Picker.Item label="No" value="No" requierd/>
                <Picker.Item label="Yes" value="Yes" />
            </Picker>

            <Text style={styles.txt5}>Alert when stock is</Text>
            <Picker selectedValue={selectedValue3} style={styles.input5}
                onValueChange={(itemValue, itemIndex) => setSelectedValue3(itemValue)}
            >
                <Picker.Item label={selectedValue3} value="0" requierd/>
                <Picker.Item label="Low" value="Low" />
                <Picker.Item label="Limited" value="Limited" />
            </Picker>


            <View style={styles.button}>
                <AntDesign name="check" size={24} color="#FFFFFF" />
                <Button title='Save' color={'#C31B20'}
                onPress={() =>
                    navigation.navigate("Inventory",sendData())} ></Button>
            </View>
            <StatusBar style="auto" />
        </View>
    </Formik>
    );
}


const deviceWidth = Math.round(Dimensions.get('window').width);
const deviceHeight = Math.round(Dimensions.get('window').height);
const styles = StyleSheet.create({
    container: {

        backgroundColor: '#FFFFFF',
        height: deviceHeight,
        alignItems: 'center',
        gap: 8,
    },
    title: {

        color: '#000000',
        fontSize: 17,
        fontFamily: 'GeneralSans-Semibold',
        fontWeight: 'bold',
        position: 'absolute',
        left: '5%',
        top: '5%',

    },
    txt: {

        color: '#000000',
        fontSize: 14,
        textDecorationLine: 'underline',
        fontFamily: 'GeneralSans-Semibold',
        position: 'absolute',
        left: '15%',
        top: '10%',
    },
    emoji:{
        fontSize: 35,
        position: 'absolute',
        left: '3%',
        top: '8%',
    },
    emojis:{
        zIndex: 1,
        top: deviceHeight/2,
       
    },
    txt1: {

        color: '#000000',
        fontSize: 14,
        fontFamily: 'GeneralSans-Semibold',
        position: 'absolute',
        left: '5%',
        top: '15%',
       
    },
    input1: {
        height: 42,
        width: '92%',
        borderColor: '#000000',
        borderWidth: 0.7,
        borderRadius: 10,
        fontFamily: 'GeneralSans-Medium',
        position: 'absolute',
        left: '5%',
        top: '18%',
        maxWidth: '92%',
        //minWidth: 

    },
    txt2: {

        color: '#000000',
        fontSize: 14,
        fontFamily: 'GeneralSans-Semibold',
        position: 'absolute',
        left: '5%',
        top: '26%',
    },
    input2: {
        height: 42,
        //width: deviceWidth - 180,
        borderColor: '#000000',
        borderWidth: 0.7,
        borderRadius: 10,
        fontFamily: 'GeneralSans-Medium',
        position: 'absolute',
        left: '5%',
        top: '29%',


        width: '55%',
        maxWidth: '55%',
        //minWidth: 300,
        alignItems: 'center'
    },
    txt3: {
        color: '#000000',
        fontSize: 14,
        fontFamily: 'GeneralSans-Semibold',
        position: 'absolute',
        left: '65%',
        top: '26%',
    },
    input3: {
        //width: deviceWidth - 260,
        height: 42,
        borderColor: '#000000',
        borderWidth: 0.7,
        borderRadius: 10,
        fontFamily: 'GeneralSans-Medium',
        position: 'absolute',
        left: '65%',
        top: '29%',

        width: '32%',
        maxWidth: '32%',
        //minWidth: 300,
        alignItems: 'center'
    },
    txt4: {

        color: '#000000',
        fontSize: 14,
        fontFamily: 'GeneralSans-Semibold',
        position: 'absolute',
        left: '5%',
        top: '37%',

    },
    input4: {
        width: '92%',
        height: 42,
        borderColor: '#000000',
        borderWidth: 0.7,
        borderRadius: 10,
        fontFamily: 'GeneralSans-Medium',
        position: 'absolute',
        left: '5%',
        top: '40%',
        maxWidth: '92%',
    },
    txt5: {

        color: '#000000',
        fontSize: 14,
        fontFamily: 'GeneralSans-Semibold',
        position: 'absolute',
        left: '5%',
        top: '48%',
    },
    input5: {
        width: '92%',
        height: 42,
        borderColor: '#000000',
        borderWidth: 0.7,
        borderRadius: 10,
        fontFamily: 'GeneralSans-Medium',
        position: 'absolute',
        left: '5%',
        top: '51%',
        maxWidth: '92%',

    },
    button: {
        backgroundColor: '#C31B20',
        borderRadius: 10,
        alignItems: 'center',
        justifyContent: 'center',
        flexDirection: 'row',
        position: 'absolute',
        left: '65%',
        top: '90%',
        width: '32%',
    },


});
