import Axios from 'axios';



const port = process.env.REACT_APP_BASE_PORT;



//==================================================
export const getInventory = async()=>{
  //let response = await Axios.post(`http://localhost:${port}/getInventory`);
  let response = await Axios.post(`http://localhost:3001/getInventory`);
  return response;
}
//==================================================
export const getProducts = async(inventoryId)=>{
  let response = await Axios.post(`http://localhost:3001/getProducts`,{inventoryId});
  return response;
}
