import React from 'react';
import { StyleSheet, View, Button, Text ,Dimensions, Image} from 'react-native';
import { AntDesign } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';
//-------------------------------------------
import seafoodFreezer from '../JSON/seafoodFreezerProduct.json';
import meetFreezer from '../JSON/meetFreezerProducts.json';
import vegetableFridge from '../JSON/vegetableFridgeProducts.json';
import chickenFridge from '../JSON/chickenFridgeProducts.json';
import pastaCabinet from '../JSON/pastaCabinetProducts.json';
import Inventory from '../JSON/inventory.json'
//================================================
var backgroundColorCard = function (card) {
    let color;
    //inventoryLevelPercent
    let typeFood = card.title1;
    let json;
    if (typeFood === 'Seafood Freezer') {
        json = seafoodFreezer;
    }

    else if (typeFood === 'Meat Freezer') {
        json = meetFreezer;
    }

    else if (typeFood === 'Vegetable Fridge') {
        json = vegetableFridge;
    }

    else if (typeFood === 'Chicken Fridge') {
        json = chickenFridge;
    }

    else if (typeFood === 'Pasta Cabinet') {
        json = pastaCabinet;
    }///////////////////////////////////
    let sumMax = 0;
    let sumMin = 0;
    for (let i = 0; i < json.products.length; i++)
    {
        let weight = json.products[i].weight;
        let min = weight.split("/", 1);

        let max = weight.slice(weight.indexOf('/') + 1, weight.lastIndexOf(' '));
        max = parseInt(max);
        min = parseInt(min);
        sumMax += max;
        sumMin += min;
    }


    let percent = sumMin / sumMax * 100;


    for (let i = 0; i < Inventory.data.length; i++) 
    {
        if (Inventory.data[i].title1 === typeFood)
        {
            Inventory.data[i].inventoryLevelPercent = percent;
        }
    }

    if (percent <= 30) {
        color = '#C41C21';
    }
    if (percent > 30 && percent <= 70) {
        color = '#EF9300';
    }
    if (percent > 70) {
        color = '#28AA29';
    }

    return color;
}

//================================================
//A function that returns the component to which we will need to render
var checkGoComponent = function (percent, unit) {
    let component;
    
    if (percent <= 30) {
        component = "StorageUnitLowStock";//מלאי נמוך
    }
    else if (percent > 30 && percent <= 70) {
        component = "StorageUnitLimitedStock";//מלאי מוגבל
    }
    else if (percent > 70) {
        component = "StorageUnitSufficientStock";//מלאי מספיק
    }
    return component;
}
//================================================
const InventoryCard = ({ route, ...props })  =>{
    const navigation = useNavigation();

    return(
        <View style={styles.cardContainer} >
            <View style={[styles.color, { backgroundColor: backgroundColorCard(props.card) }, { width: props.card.inventoryLevelPercent + '%' }]}></View>

            <Text style={styles.imageStyle}>{props.card.emoji}</Text>

            <View style={styles.textStyle}>
                <Text style={styles.textStyle1}>{props.card.title1}</Text>
                <Text style={styles.textStyle2}>{props.card.title2}</Text>
                <Text style={styles.textStyle3}>{props.card.title3}</Text>
            </View>

            <AntDesign style={styles.iconStyle} name="arrowright" size={24} color="black" 
                onPress={() =>
                    navigation.navigate(checkGoComponent(props.card.inventoryLevelPercent, props.card.unit), { typeFood: props.card.title1, unit: props.card.unit, temperature: props.card.temperature, emoji: props.card.emoji })}
            />
        </View>
        
    )
}

const deviceWidth = Math.round(Dimensions.get('window').width);
const styles = StyleSheet.create({
    cardContainer: {
        backgroundColor: '#FFFFFF',
        width: deviceWidth - 30,
        height: 90,
        borderRadius: 30,
        shadowColor: '#00000029',
        shadowRadius: 30,
        flexDirection: 'row',
        top: '30%',
    },
    color:{
        opacity: 0.26,
        


    },
    imageStyle:{
        position: 'absolute',
        left: '8%',
        top: '25%',
        flex: 1,
        fontSize:38
    },
    textStyle:{
        flex: 2,
        position: 'absolute',
        left: '30%',
        top: '20%'
    },
    textStyle1:{
        fontFamily: 'GeneralSans',
        fontSize: 16,
        fontWeight: 'bold',
        color: '#000000',
    },
    textStyle2: {
        fontFamily: 'GeneralSans-Semibold',
        fontSize: 14,
        color: '#A2142C',
        fontWeight: 'bold',

    },
    textStyle3: {
        fontFamily: 'GeneralSans-Medium',
        fontSize: 11,
        fontWeight: 'bold',
        color: '#000000',

    },
    iconStyle:{
        flex: 3,
        position: 'absolute',
        left: '90%',
        top: '30%'
    }
});



export default InventoryCard;
