import React from 'react';
import { StyleSheet, View, Button, Text, Image, Dimensions } from 'react-native';
import { useNavigation } from '@react-navigation/native';

//====================================================================





//====================================================================
const StorageUnitCard = ({ route, ...props }) => {
    const navigation = useNavigation();

    return (
        <View style={styles.cardContainer} >

            <View style={styles.line1Style}>
                <Text style={styles.nameStyle}>{props.card.name}</Text>
                <Text style={styles.rectangle}>{props.card.weight}</Text>
            </View>

            

            
        </View>
    )
}
const deviceWidth = Math.round(Dimensions.get('window').width);
const styles = StyleSheet.create({
    cardContainer: {
        backgroundColor: '#FFFFFF',
        alignItems: 'center',
        gap: 10,
    },
    line1Style:{
        flexDirection: 'row',
        justifyContent: 'space-between',
        //width: '90%',
    },
    nameStyle:{
        fontFamily: 'GeneralSans-Semibold',
    },
    rectangle:{
        backgroundColor: '#C31B20',
    }
});

export default StorageUnitCard;
