import React from 'react';
import { StyleSheet, View, Button, Text, Dimensions, TextInput, Picker } from 'react-native';
import { useState, useEffect } from 'react';
import {createProduct} from '../axios/accessServer';

const AddProduct = ({ route, ...props }) => {
    const deviceWidth = Math.round(Dimensions.get('window').width);
    const deviceHeight = Math.round(Dimensions.get('window').height);

    const [productName, setProductName] = useState('');
    const [quantity, setQuantity] = useState('');
    const [unit, setUnit] = useState('');
    const [expirationDate, setExpirationDate] = useState('');
    const [deliveryDate, setDeliveryDate] = useState('');


    const [isChecked, setIsChecked] = useState(false);
    const [alertValue, setAlertValue] = useState('');
    //===========================================================
    const sendData = async ()=>{
        let json = {
            Inventoryid: route.params.Inventoryid,
            productName: productName,
            quantity: quantity,
            brand: "nike",
            unit: unit,
            expirationDate: expirationDate,
            deliveryDate: deliveryDate,
            isChecked: isChecked,
            alertValue: alertValue

        };
        await createProduct(json);
    }
//================================================
    return (
        <View style={styles.container}>
            <Text style={styles.tytle}>Add a product</Text>
            <Row label="Product Name:">
                <TextInput style={styles.input} onChangeText={text => setProductName(text)} />
            </Row>
            
            
            <Row label="Quantity:">
                <TextInput style={styles.input} onChangeText={text => setQuantity(text)} />
            </Row>
            <Row label="Unit:">
                <TextInput style={styles.input} onChangeText={text => setUnit(text)} />
            </Row>
            <Row label="Expiration Date:">
                <TextInput style={styles.input} onChangeText={text => setExpirationDate(text)} />
            </Row>
            <Row label="Delivery Date:">
                <TextInput style={styles.input} onChangeText={text => setDeliveryDate(text)} />
            </Row>
            
            <Row label="Alert when below:">
                <TextInput
                    style={styles.input}
                    placeholder="Enter alert value"
                    value={alertValue}
                    onChangeText={text => setAlertValue(text)}
                />
            </Row>
            <Row>
                <input
                    type="checkbox"
                    checked={isChecked}
                    onChange={() => setIsChecked(!isChecked)}
                />
                <Text style={styles.label}>Alert when low on stock</Text>
            </Row>

            <View >
                <Button title='Save' color={'#C31B20'}
                onPress={() =>
                    navigation.navigate("Inventory",sendData())} ></Button>
            </View>
        </View>
    );
}

const Row = ({ label, children }) => (
    <View style={styles.row}>
        <Text style={styles.label}>{label}</Text>
        {children}
    </View>
);

const styles = StyleSheet.create({
    container: {
        backgroundColor: '#FFFFFF',
        width: '100%',
        borderRadius: 15,
        shadowColor: '#0000009D',
        shadowRadius: 30,
        flexDirection: 'column',
        flex: 1,
        justifyContent: 'center',
    },
    tytle:{
        textAlign: 'center',
        fontWeight: 'bold',
        fontSize: 23,
    },
    row: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        marginBottom: 5,
    },
    label: {
        fontSize: 16,
        fontWeight: 'bold',
        marginRight: 10,
    },
    input: {
        flex: 1,
        borderWidth: 1,
        borderColor: '#000000',
        borderRadius: 5,
        paddingHorizontal: 10,
        paddingVertical: 5,
    },
    selectInput: {
        flex: 1,
        borderWidth: 1,
        borderColor: '#000000',
        borderRadius: 5,
        marginRight: 10,
    },
});

export default AddProduct;
