import React from 'react';
import { StyleSheet, View, Button, Text ,Dimensions, Image} from 'react-native';
import { AntDesign } from '@expo/vector-icons';

const InventoryCard = props =>{
    return(
        <View style={styles.cardContainer}>
            <Image style={styles.imageStyle} source={require('../images/fish.jpg')}/>

            <View style={styles.textStyle}>
                <Text style={styles.textStyle1}>{props.card.title1}</Text>
                <Text style={styles.textStyle2}>{props.card.title2}</Text>
                <Text style={styles.textStyle3}>{props.card.title3}</Text>
            </View>

            <AntDesign style={styles.iconStyle} name="arrowright" size={24} color="black" />
        </View>
        
    )
}

const deviceWidth = Math.round(Dimensions.get('window').width);
const styles = StyleSheet.create({
    cardContainer: {
        width: deviceWidth - 30,
        height: 90,
        borderRadius: 30,
        shadowColor: '#00000029',
        shadowRadius: 30,
        flexDirection: 'row',
    },
    imageStyle:{
        position: 'absolute',
        left: '10%',
        top: '20%',
        height: 50,
        width: 38,
        flex: 1,
    },
    textStyle:{
        flex: 2,
        position: 'absolute',
        left: '30%',
        top: '20%'
    },
    textStyle1:{
        fontFamily: 'General Sans',
        fontSize: 16,
        fontWeight: 'bold',
        color: '#000000',
    },
    textStyle2: {
        fontFamily: 'General Sans, Semibold',
        fontSize: 14,
        color: '#A2142C',
    },
    textStyle3: {

    },
    iconStyle:{
        flex: 3,
        position: 'absolute',
        left: '90%',
        top: '30%'
    }
});



export default InventoryCard;
