import React from 'react';
import { StyleSheet, View, Button, Text, Dimensions, TextInput, Picker } from 'react-native';
import { useState, useEffect } from 'react';

const AddProduct = () => {
    const deviceWidth = Math.round(Dimensions.get('window').width);
    const deviceHeight = Math.round(Dimensions.get('window').height);

    const [productName, setProductName] = useState('');
    const [selectedValue, setSelectedValue] = useState('');
    const [isChecked, setIsChecked] = useState(false);
    const [alertValue, setAlertValue] = useState('');

    return (
        <View style={styles.container}>
            <Row label="Product Name:">
                <TextInput style={styles.input} onChangeText={text => setProductName(text)} />
            </Row>
            <Row label="Category:">
                <Picker
                    style={styles.selectInput}
                    selectedValue={selectedValue}
                    onValueChange={(itemValue, itemIndex) =>
                        setSelectedValue(itemValue)
                    }
                >
                    <Picker.Item label="Option 1" value="option1" />
                    <Picker.Item label="Option 2" value="option2" />
                    <Picker.Item label="Option 3" value="option3" />
                </Picker>
            </Row>
            <Row label="Quantity:">
                <TextInput style={styles.input} placeholder="Enter quantity" />
            </Row>
            <Row label="Unit:">
                <TextInput style={styles.input} placeholder="Enter unit" />
            </Row>
            <Row label="Price:">
                <TextInput style={styles.input} placeholder="Enter price" />
            </Row>
            <Row label="Label:">
                <TextInput style={styles.input} placeholder="Enter label" />
            </Row>
            <Row label="Expiration Date:">
                <TextInput style={styles.input} placeholder="Enter expiration date" />
            </Row>
            <Row label="Delivery Date:">
                <TextInput style={styles.input} placeholder="Enter delivery date" />
            </Row>
            <Row label="Alert when below:">
                <TextInput
                    style={styles.input}
                    placeholder="Enter alert value"
                    value={alertValue}
                    onChangeText={text => setAlertValue(text)}
                />
            </Row>
            <Row>
                <input
                    type="checkbox"
                    checked={isChecked}
                    onChange={() => setIsChecked(!isChecked)}
                />
                <Text style={styles.label}>Alert when low on stock</Text>
            </Row>
        </View>
    );
}

const Row = ({ label, children }) => (
    <View style={styles.row}>
        <Text style={styles.label}>{label}</Text>
        {children}
    </View>
);

const styles = StyleSheet.create({
    container: {
        backgroundColor: '#FFFFFF',
        width: '100%',
        borderRadius: 15,
        shadowColor: '#0000009D',
        shadowRadius: 30,
        flexDirection: 'column',
        flex: 1,
        justifyContent: 'center',
    },
    row: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        marginBottom: 10,
    },
    label: {
        fontSize: 16,
        fontWeight: 'bold',
        marginRight: 10,
    },
    input: {
        flex: 1,
        borderWidth: 1,
        borderColor: '#000000',
        borderRadius: 5,
        paddingHorizontal: 10,
        paddingVertical: 5,
    },
    selectInput: {
        flex: 1,
        borderWidth: 1,
        borderColor: '#000000',
        borderRadius: 5,
        marginRight: 10,
    },
});

export default AddProduct;
