import React from 'react';
import { StyleSheet, View, Button, Text, Image, Dimensions } from 'react-native';
import Card from './InventoryCard';
import { Picker } from '@react-native-picker/picker';
import { useState } from 'react';
import cards from '../JSON/inventory.json'
//====================================================================

//====================================================================
const Inventory = ({navigation}) => {
    const [selectedSort, setSelectedSort] = useState();

    return(
        <View style={styles.container}>

            <View style={styles.titleAndPicker}>
                <Text style={styles.titleStile}>Manage Inventory </Text>

                <Text>Sort by</Text><Picker style={styles.pickerStyle}
                    selectedValue={selectedSort}
                    onValueChange={(itemValue, itemIndex) =>
                        setSelectedSort(itemValue)
                        }>
                    
                    <Picker.Item label="Name" value="1" />
                    <Picker.Item label="Stock Level" value="2" />
                    <Picker.Item label="Type" value="3" />
                </Picker>
            </View>

            {cards.data.map((card, key) => {
                return (
                    <View key={card.id} >
                        <Card card={card}/>
                    </View>
                );
            })}
            
            <View style={styles.buttonStyle}>
                <Button color="#C3001F" onPress={() => 
                        navigation.navigate('AddStorageUnit')} title='+ Add Storage Unit'></Button>
            </View>
            
        </View>
    );
}
const deviceWidth = Math.round(Dimensions.get('window').width);
const deviceHeight = Math.round(Dimensions.get('window').height);

const styles = StyleSheet.create({
    container: {
        height: deviceHeight,
        alignItems: 'center',
        gap: 8,
        backgroundColor: '#FFFFFF',
    },
    titleAndPicker:{
        flexDirection: 'row',
        justifyContent: 'space-between',
        width:'90%',
        top: '3%',
    },
    titleStile:{
        fontFamily: 'GeneralSans-Semibold',
        fontSize:17,
        fontWeight: 'bold',

    },
    pickerStyle:{
        borderRadius: 10,
        shadowColor: '#00000022',
        borderWidth: 0, 
        overflow: "hidden", 
        shadowRadius: 11,
        fontWeight: 'bold',
        fontFamily: 'GeneralSans-Bold',

    },
    
    buttonStyle: {
        fontFamily: 'GeneralSans',
        fontWeight: 'bold',
        position: 'absolute',
        top: '85%',
        width: deviceWidth - 150,
        borderRadius: 15,
        
        backgroundColor: '#C3001F',
        borderRadius: 15,
        alignItems: 'center',
        justifyContent: 'center',
    }
});

export default Inventory;
