import React from 'react';
import { StyleSheet, View, Button, Text, Dimensions, TextInput, Picker } from 'react-native';
import { useState, useEffect } from 'react';

const AddProduct = () => {
    const deviceWidth = Math.round(Dimensions.get('window').width);
    const deviceHeight = Math.round(Dimensions.get('window').height);

    const [selectedValue, setSelectedValue] = useState('');
    const [selectedDate, setSelectedDate] = useState('');

    const handleDateChange = (event) => {
      setSelectedDate(event.target.value);
    };

    return (
        <View style={styles.container}>
            <View style={styles.row}>
                <Text style={styles.label}>Product Name:</Text>
                <TextInput style={styles.input} placeholder="Enter product name" />
            </View>
            <View style={styles.row}>
                <Text style={styles.label}>Category:</Text>
                <Picker
                    style={styles.selectInput}
                    selectedValue={selectedValue}
                    onValueChange={(itemValue, itemIndex) =>
                        setSelectedValue(itemValue)
                    }
                >
                    <Picker.Item label="Option 1" value="option1" />
                    <Picker.Item label="Option 2" value="option2" />
                    <Picker.Item label="Option 3" value="option3" />
                </Picker>
            </View>
            <View style={styles.row}>
                <Text style={styles.label}>Quantity:</Text>
                <TextInput style={styles.input} placeholder="Enter quantity" />
            </View>
            <View style={styles.row}>
                <Text style={styles.label}>Unit:</Text>
                <TextInput style={styles.input} placeholder="Enter unit" />
            </View>
            <View style={styles.row}>
                <Text style={styles.label}>Price:</Text>
                <TextInput style={styles.input} placeholder="Enter price" />
            </View>
            <View style={styles.row}>
                <Text style={styles.label}>Label:</Text>
                <TextInput style={styles.input} placeholder="Enter label" />
            </View>
            <View style={styles.row}>
                <Text style={styles.label}>Expiration Date:</Text>
                <TextInput style={styles.input} placeholder="Enter expiration date" />
            </View>
            <View style={styles.row}>
                <Text style={styles.label}>Delivery Date:</Text>
                <TextInput style={styles.input} placeholder="Enter delivery date" />
            </View>

            <div>
      <label htmlFor="date-input">Choose a date:</label>
      <input
        type="date"
        id="date-input"
        value={selectedDate}
        onChange={handleDateChange}
      />
    </div>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        backgroundColor: '#FFFFFF',
        width: '100%',
        borderRadius: 15,
        shadowColor: '#0000009D',
        shadowRadius: 30,
        flexDirection: 'column',
        flex: 1,
        maxWidth: 600,
        justifyContent: 'center',
    },
    row: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        marginBottom: 10,
    },
    label: {
        fontSize: 16,
        fontWeight: 'bold',
        marginRight: 10,
    },
    input: {
        flex: 1,
        borderWidth: 1,
        borderColor: '#000000',
        borderRadius: 5,
        paddingHorizontal: 10,
        paddingVertical: 5,
    },
    selectInput: {
        flex: 1,
        borderWidth: 1,
        borderColor: '#000000',
        borderRadius: 5,
        marginRight: 10,
    },
});

export default AddProduct;
