import React from 'react';
import { StyleSheet, View, Button, Text, Image, Dimensions } from 'react-native';
import { useState, useEffect } from 'react';
import Emoji from 'react-native-emoji';

//====================================================================
//====================================================================
const EmojiComp = ({navigation}) => {



    return(
        <View style={styles.container}>
            <Emoji name="coffee" style={styles.emoji} />
            <Emoji name="tea" style={styles.emoji} />
            <Emoji name="banana" style={styles.emoji} />
            <Emoji name="apple" style={styles.emoji} />
            <Emoji name="grapes" style={styles.emoji} />
            <Emoji name="peach" style={styles.emoji} />
            <Emoji name="melon" style={styles.emoji} />
            <Emoji name="watermelon" style={styles.emoji} />
            <Emoji name="tangerine" style={styles.emoji} />
            <Emoji name="lemon" style={styles.emoji} />
            <Emoji name="pineapple" style={styles.emoji} />
            <Emoji name="pear" style={styles.emoji} />
            <Emoji name="cherries" style={styles.emoji} />
            <Emoji name="strawberry" style={styles.emoji} />
            <Emoji name="broccoli" style={styles.emoji} />
            <Emoji name="mushroom" style={styles.emoji} />
            <Emoji name="pretzel" style={styles.emoji} />
            <Emoji name="hamburger" style={styles.emoji} />
            <Emoji name="pizza" style={styles.emoji} />
            <Emoji name="taco" style={styles.emoji} />
            <Emoji name="Cooking" style={styles.emoji} />


            
        </View>
    );
}
const deviceWidth = Math.round(Dimensions.get('window').width);
const deviceHeight = Math.round(Dimensions.get('window').height);

const styles = StyleSheet.create({
    container: {
        backgroundColor: '#FFFFFF',
        width: deviceWidth - 100,
        borderRadius: 15,
        shadowColor: '#00000029',
        shadowRadius: 15,
        flexDirection:'row',
        flex: 1,
        flexWrap: 'wrap',
    },
    emoji:{
        fontSize: 20,
        marginHorizontal: 5,
        
    }
});

export default EmojiComp;
